INSERT INTO `events` (`eid`, `etitle`, `etext`, `etag`, `AlbumId`, `status`, `created_at`, `updated_at`) VALUES
('1', '𝗜𝗡𝗧𝗥𝗢𝗩𝗔 𝟭.𝟬 - 𝗪𝗲𝗯 & 𝗥𝗼𝗯𝗼𝘁𝗶𝗰𝘀 𝗖𝗼𝗺𝗽𝗲𝘁𝗶𝘁𝗶𝗼𝗻 𝗢𝗿𝗴𝗮𝗻𝗶𝘇𝗲 𝗕𝘆 𝗣𝘂𝗹𝘇𝗮𝗿 𝗖𝗹𝘂𝗯 - 𝗦𝗟𝗜𝗔𝗧𝗘 𝗚𝗮𝗹𝗹𝗲 - 𝗗𝗲𝗽𝗮𝗿𝘁𝗺𝗲𝗻𝘁 𝗼𝗳 𝗜𝗻𝗳𝗼𝗿𝗺𝗮𝘁𝗶𝗼𝗻 𝗧𝗲𝗰𝗵𝗻𝗼𝗹𝗼𝗴𝘆.', '<div class=\"xdj266r x11i5rnm xat24cr x1mh8g0r x1vvkbs x126k92a\">\r\n<div dir=\"auto\"><span class=\"html-span xexx8yu x4uap5 x18d9i69 xkhd6sd x1hl2dhg x16tdsg8 x1vvkbs x3nfvp2 x1j61x8r x1fcty0u xdj266r xat24cr xgzva0m xhhsvwb xxymvpz xlup9mm x1kky2od\"><img class=\"xz74otr x168nmei x13lgxp2 x5pf9jr xo71vjh\" src=\"https://static.xx.fbcdn.net/images/emoji.php/v9/tde/1/16/1f4f8.png\" alt=\"📸\" width=\"16\" height=\"16\"></span> Unveiling Creativity and Innovation!</div>\r\n<div dir=\"auto\">Our campus exhibition brought ideas to life with groundbreaking projects in robotics creative websites , and thrilling games all developed by our talented batchmates.</div>\r\n</div>\r\n<div class=\"x11i5rnm xat24cr x1mh8g0r x1vvkbs xtlvy1s x126k92a\">\r\n<div dir=\"auto\">Adding to the excitement, we hosted an electrifying gaming competition, proudly sponsored by SellX Computers, Pvt. Ltd.</div>\r\n</div>\r\n<div class=\"x11i5rnm xat24cr x1mh8g0r x1vvkbs xtlvy1s x126k92a\">\r\n<div dir=\"auto\">Dive into the moments of innovation and competition through this photo album! Your support means the world. Let us know your favorite highlights in the comments! <span class=\"html-span xexx8yu x4uap5 x18d9i69 xkhd6sd x1hl2dhg x16tdsg8 x1vvkbs x3nfvp2 x1j61x8r x1fcty0u xdj266r xat24cr xgzva0m xhhsvwb xxymvpz xlup9mm x1kky2od\"><img class=\"xz74otr x168nmei x13lgxp2 x5pf9jr xo71vjh\" src=\"https://static.xx.fbcdn.net/images/emoji.php/v9/t6e/1/16/1f4ac.png\" alt=\"💬\" width=\"16\" height=\"16\"></span></div>\r\n</div>', 'Introva, pulzar, Vihanga', 'album_6824fd3fd5416', 1, '2025-05-14 20:29:51', '2025-05-15 05:55:53'),
